import React, { useRef, useState } from "react";
import { UploadIcon } from "./icons";

console.log("‚úÖ Dashboard.js loaded");

function Dashboard() {
  const fileInputRef = useRef(null);
  const [file, setFile] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [report, setReport] = useState(null);

  const acceptedTypes = [
    "py", "js", "ts", "java", "cpp", "cs", "go", "rs", "kt", "zip", "tar.gz",
  ];

  // Handle file selection
  const handleFileChange = (e) => {
    const chosenFile = e.target.files?.[0];
    if (!chosenFile) return;

    const ext = chosenFile.name.split(".").pop()?.toLowerCase() || "";
    if (!acceptedTypes.includes(ext)) {
      setError("Unsupported file type");
      setFile(null);
      e.target.value = "";
      return;
    }

    setError("");
    setFile(chosenFile);
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  // Call the backend API and display full report
  const handleSubmit = async () => {
    if (!file) return;
    console.log("üì§ Uploading file to backend‚Ä¶");
    setLoading(true);
    setError("");
    setReport(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://127.0.0.1:8000/review", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Server returned ${response.status}`);
      }

      const data = await response.json();
      console.log("‚úÖ Full backend response:", data);
      setReport(data); // show full report object
    } catch (err) {
      console.error(err);
      setError("Failed to generate report. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center text-center px-4 w-full max-w-2xl space-y-8">
      {/* Title */}
      <h1 className="text-4xl md:text-5xl font-extrabold text-gray-900 drop-shadow-sm">
        Code Review Assistant
      </h1>

      {/* Upload bar */}
      <div
        onClick={handleUploadClick}
        className="flex items-center w-full bg-white/90 shadow-md rounded-xl px-4 py-4 cursor-pointer border border-gray-200 hover:border-blue-400 focus-within:ring-2 focus-within:ring-blue-300 transition duration-200"
        role="button"
        aria-label="Upload your code file"
      >
        <div className="flex items-center justify-center bg-blue-100 text-blue-600 rounded-lg w-10 h-10 mr-3">
          <UploadIcon />
        </div>
        <div className="flex-1 text-left text-gray-500 truncate">
          {file ? file.name : "Upload your code file"}
        </div>
        <input
          type="file"
          accept=".py,.js,.ts,.java,.cpp,.cs,.go,.rs,.kt,.zip,.tar.gz"
          ref={fileInputRef}
          onChange={handleFileChange}
          className="hidden"
        />
      </div>

      {/* Error message */}
      {error && <p className="text-red-600 text-sm">{error}</p>}

      {/* Submit button */}
      <button
        onClick={handleSubmit}
        disabled={!file || loading}
        className={`w-72 h-14 text-lg font-semibold text-white rounded-full transition-all duration-300 focus:ring-2 focus:ring-offset-2 focus:ring-blue-400
          ${
            file && !loading
              ? "bg-gradient-to-r from-[#4EA7FF] to-[#0A4FBF] hover:brightness-110"
              : "bg-gray-400 cursor-not-allowed"
          }`}
      >
        {loading ? (
          <span className="flex items-center justify-center gap-2">
            <svg
              className="animate-spin h-5 w-5 text-white"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
            >
              <circle
                className="opacity-25"
                cx="12"
                cy="12"
                r="10"
                stroke="currentColor"
                strokeWidth="4"
              ></circle>
              <path
                className="opacity-75"
                fill="currentColor"
                d="M4 12a8 8 0 018-8v4a4 4 0 00-4 4H4z"
              ></path>
            </svg>
            Generating‚Ä¶
          </span>
        ) : (
          "View Report"
        )}
      </button>

      {/* Report section */}
      {report && (
        <div className="mt-8 w-full bg-white/95 rounded-xl shadow-lg text-left p-6 space-y-4 border border-gray-200 animate-fadeIn overflow-y-auto max-h-[70vh]">
          <h2 className="text-2xl font-semibold text-blue-600">
            üßæ Full Code Review Report
          </h2>

          {/* If your backend returns structured analysis */}
          {report?.report?.analysis ? (
            <>
              <p className="text-gray-700">
                {report.report.analysis.summary || "No summary provided."}
              </p>

              {report.report.analysis.readability_score && (
                <p className="text-sm text-gray-600">
                  üìñ Readability Score:{" "}
                  <strong>{report.report.analysis.readability_score}</strong>
                </p>
              )}

              {report.report.analysis.metrics && (
                <div>
                  <h3 className="font-semibold text-gray-800 mt-3">üìä Metrics</h3>
                  <pre className="bg-gray-100 p-3 rounded-lg text-sm text-gray-800 overflow-x-auto whitespace-pre-wrap">
                    {JSON.stringify(report.report.analysis.metrics, null, 2)}
                  </pre>
                </div>
              )}

              {report.report.analysis.suggestions?.length > 0 && (
                <div>
                  <h3 className="font-semibold text-gray-800 mt-3">üí° Suggestions</h3>
                  <ul className="list-disc list-inside text-gray-700 text-sm space-y-1">
                    {report.report.analysis.suggestions.map((s, i) => (
                      <li key={i}>{s.message || s}</li>
                    ))}
                  </ul>
                </div>
              )}

              {report.report.analysis.issues?.length > 0 && (
                <div>
                  <h3 className="font-semibold text-gray-800 mt-3">‚ö†Ô∏è Issues</h3>
                  <ul className="list-disc list-inside text-gray-700 text-sm space-y-1">
                    {report.report.analysis.issues.map((issue, i) => (
                      <li key={i}>
                        <strong>Line {issue.line}:</strong> {issue.message}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </>
          ) : (
            // fallback for any unexpected structure
            <pre className="bg-gray-100 p-3 rounded-lg text-sm text-gray-800 overflow-x-auto whitespace-pre-wrap break-words">
              {JSON.stringify(report, null, 2)}
            </pre>
          )}
        </div>
      )}

      {/* Footer */}
      <footer className="text-sm text-white/80 mt-8">Made by Supriya</footer>
    </div>
  );
}

export default Dashboard;
